mapper.sql:

	    from
			code a
			left join codegroup b on a.codegroup_cdgrSeq = b.cdgrSeq
		where 1=1
		AND a.codeUseNy = #{shUseNy}
        AND a.codeDelNy = #{shDelNy}

					AND a.codeRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.codeModDate BETWEEN #{shDateStart} AND #{shDateEnd}


            AND b.cdgrSeq = #{shValue}
            AND b.cdgrName LIKE CONCAT('%',#{shValue},'%')
            AND b.cdgrNameEng LIKE CONCAT('%',#{shValue},'%')
            AND a.codeSeq LIKE CONCAT('%',#{shValue},'%')
            AND a.codeName LIKE CONCAT('%',#{shValue},'%')
            AND a.codeNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into code (
	codeName
	,codeNameEng
	,codeUseNy
	,codeRegDate
	,codeDesc
	,codegroup_cdgrSeq
) values (
	#{codeName}
	,#{codeNameEng}
	,#{codeUseNy}
	,CURRENT_TIMESTAMP
	,#{codeDesc}
	,#{codegroup_cdgrSeq}
)

	SELECT last_insert_id()

mapper.update:
update code set
	codeName = #{codeName}
	,codeNameEng = #{codeNameEng}
	,codeUseNy = #{codeUseNy}
	,codeModDate = CURRENT_TIMESTAMP
	,codeDesc = #{codeDesc}
	,codegroup_cdgrSeq = #{codegroup_cdgrSeq}
where
	codeSeq = #{codeSeq}
