mapper.sql:

	    from
			dealer
		where 1=1
		AND dealUseNy = #{shUseNy}
        AND dealDelNy = #{shDelNy}

					AND dealRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
					AND dealModDate BETWEEN #{shDateStart} AND #{shDateEnd}


            AND dealName LIKE CONCAT('%',#{shValue},'%')
            AND dealAddress LIKE CONCAT('%',#{shValue},'%')
            AND dealManager LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into dealer (
	dealName
	,dealAddress
	,dealManager
	,dealManagerTel
	,dealUseNy
	,dealDelNy
	,dealRegDate
	,dealModDate
	,dealDesc
) values (
	#{dealName}
	,#{dealAddress}
	,#{dealManager}
	,#{dealManagerTel}
	,#{dealUseNy}
	,#{dealDelNy}
	,CURRENT_TIMESTAMP
	,CURRENT_TIMESTAMP
	,#{dealDesc}
)

	SELECT last_insert_id()

mapper.update:
update dealer set
	dealName = #{dealName}
	,dealAddress = #{dealAddress}
	,dealManager = #{dealManager}
	,dealManagerTel = #{dealManagerTel}
	,dealUseNy = #{dealUseNy}
	,dealDelNy = #{dealDelNy}
	,dealModDate = CURRENT_TIMESTAMP
where
	dealSeq = #{dealSeq}
