mapper.sql:
from delivery deli left join productorder pdor ON deli.productorder_pdorSeq = pdor.pdorSeq WHERE 1=1
AND pdor.pdorNum LIKE CONCAT('%',#{shValue},'%') AND deli.deliSeq LIKE CONCAT('%',#{shValue},'%')
AND deli.deliRegDate BETWEEN #{shDateStart} AND #{shDateEnd} AND deli.deliModDate BETWEEN #{shDateStart} AND #{shDateEnd}
