mapper.sql:
	from
	  delivery deli
	  left join productorder pdor ON deli.productorder_pdorSeq = pdor.pdorSeq
	WHERE 1=1 

            AND pdor.pdorNum = #{shValue}
            AND deli.deliNum = #{shValue}
            AND (SELECT a.mafaNum FROM manufacture a WHERE a.productorder_pdorSeq = pdor.pdorSeq limit 1) = #{shValue}


			AND deli.deliRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
