mapper.sql:

	    FROM incoming as a 
			left join manufacture as m on a.manufacture_mafaSeq = m.mafaSeq
			left join productorder as p on m.productorder_pdorSeq = p.pdorSeq
		where 1=1

		AND a.incoStatusCd = #{shStatus}


			AND incoRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND incoModDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND incoNum = #{shValue}
            AND mafaNum = #{shValue}


	    FROM incoming as a 
			left join manufacture as m on a.manufacture_mafaSeq = m.mafaSeq
		where 1=1
			AND incoStatusCd = 12

			AND incoRegDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND incoNum = #{shValue}
            AND mafaNum = #{shValue}
