mapper.sql:

	    FROM manufacture AS m
			LEFT JOIN productorder AS po ON m.productorder_pdorSeq = po.pdorSeq
		where 1=1

			AND mafaRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND mafaModDate BETWEEN #{shDateStart} AND #{shDateEnd}


            AND mafaNum = #{shValue}
            AND prdtName LIKE CONCAT('%',#{shValue},'%')


	    FROM manufacture AS m
			LEFT JOIN productorderlist AS pol ON m.productorderlist_pdolSeq = pol.pdolSeq
			LEFT JOIN product AS p ON pol.product_prdtSeq = p.prdtSeq
			LEFT JOIN productorder AS po ON pol.productorder_pdorSeq = po.pdorSeq
		where 1=1

			AND mafaRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND mafaModDate BETWEEN #{shDateStart} AND #{shDateEnd}


            AND mafaNum = #{shValue}
            AND prdtName LIKE CONCAT('%',#{shValue},'%')
