mapper.sql:

	    from
			usercustomer
		where 1=1
		AND isBlack = #{shBlackNy}
        AND delNy = #{shDelNy}

					AND userInstDate BETWEEN #{shDateStart} AND #{shDateEnd}
					AND userUpdtDate BETWEEN #{shDateStart} AND #{shDateEnd}


            AND seq = #{shValue}
            AND userCate LIKE CONCAT('%',#{shValue},'%')
            AND userId LIKE CONCAT('%',#{shValue},'%')
            AND userName LIKE CONCAT('%',#{shValue},'%')
            AND userGender LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into usercustomer (
	userId
    ,userPassword
    ,userCate
    ,userName
    ,userTel
    ,userEmail
    ,userDob
    ,userGender
    ,isBlack
    ,delNy
    ,userInstDate
) values (
	#{userId}
	,#{userPassword}
	,3
	,#{userName}
	,#{userTel}
	,#{userEmail}
	,#{userDob}
	,#{userGender}
	,0
	,0
	,CURRENT_TIMESTAMP
)

	SELECT last_insert_id()

mapper.update:
update usercustomer set
	userPassword = #{userPassword}
    ,userCate = #{userCate}
    ,userName = #{userName}
    ,userTel = #{userTel}
    ,userEmail = #{userEmail}
    ,userDob = #{userDob}
    ,userGender = #{userGender}
    ,userAddressCd = #{userAddressCd}
    ,userNote = #{userNote}
    ,userGps = #{userGps}
    ,userIp = #{userIp}
    ,shopComment = #{shopComment}
    ,noshow = #{noshow}
    ,isBlack = #{isBlack}
    ,delNy = #{delNy}
    ,userUpdtDate = CURRENT_TIMESTAMP
where 1=1
	and seq = #{seq}

update usercustomer set
	userPassword = #{userPassword}
    ,userUpdtDate = CURRENT_TIMESTAMP
where 1=1
	and seq = #{seq}
