mapper.sql:

	    from
			product a

		where 1=1
		AND a.prdtUseNy = #{prdtUseNy}
        AND a.prdtDelNy = #{prdtDelNy}

					AND a.prdtRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.prdtModDate BETWEEN #{shDateStart} AND #{shDateEnd}


            AND a.prdtName LIKE CONCAT('%',#{shValue},'%')
            AND a.prdtSN LIKE CONCAT('%',#{shValue},'%')
            AND a.prdtCateCd LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into product (
	prdtName
	,prdtSN
	,prdtPrice
	,prdtCateCd
	,prdtUseNy
	,prdtDelNy
	,prdtRegDate
	,prdtModDate
	,prdtDesc
) values (
	#{prdtName}
	,#{prdtSN}
	,#{prdtPrice}
	,#{prdtCateCd}
	,#{prdtUseNy}
	,#{prdtDelNy}
	,CURRENT_TIMESTAMP
	,CURRENT_TIMESTAMP
	,#{prdtDesc}
)

	SELECT last_insert_id()

mapper.update:
update code set
	codeName = #{codeName}
	,codeNameEng = #{codeNameEng}
	,codeUseNy = #{codeUseNy}
	,codeModDate = CURRENT_TIMESTAMP
	,codeDesc = #{codeDesc}
	,codegroup_cdgrSeq = #{codegroup_cdgrSeq}
where
	codeSeq = #{codeSeq}
