mapper.sql:
from productorder pdor left join productorderlist pdol ON pdol.productorder_pdorSeq = pdor.pdorSeq left join dealer d ON pdor.dealer_dealSeq = d.dealSeq WHERE 1=1 AND pdor.pdorDelNy = 0 AND pdor.pdorStatusCd = 3
AND pdorRegDate BETWEEN #{shDateStart} AND #{shDateEnd} AND pdorModDate BETWEEN #{shDateStart} AND #{shDateEnd}
AND pdorNum = #{shValue} AND dealName LIKE CONCAT('%',#{shValue},'%')
