mapper.insert:
insert into code (
	codeName
	,codeNameEng
	,codeUseNy
	,codeRegDate
	,codeDesc
	,codegroup_cdgrSeq
) values (
	#{codeName}
	,#{codeNameEng}
	,#{codeUseNy}
	,CURRENT_TIMESTAMP
	,#{codeDesc}
	,#{codegroup_cdgrSeq}
)

	SELECT last_insert_id()

mapper.update:
update code set
	codeName = #{codeName}
	,codeNameEng = #{codeNameEng}
	,codeUseNy = #{codeUseNy}
	,codeModDate = CURRENT_TIMESTAMP
	,codeDesc = #{codeDesc}
	,codegroup_cdgrSeq = #{codegroup_cdgrSeq}
where
	codeSeq = #{codeSeq}
