mapper.sql:

	    from codegroup as a
	    where 1=1
	    AND a.cdgUseNy = #{shUseNy}
        AND a.cdgDelNy = #{shDelNy}


					AND a.cdgInstDate BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.cdgUpdtDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.cdgSeq = #{shValue}
            AND a.cdgName LIKE CONCAT('%',#{shValue},'%')
            AND a.cdgNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into codegroup (
	cdgName
	,cdgNameEng
	,cdgUseNy
	,cdgInstDate
	,cdgDesc
) values (
	#{cdgName}
	,#{cdgNameEng}
	,#{cdgUseNy}
	,CURRENT_TIMESTAMP
	,#{cdgDesc}
)

	SELECT last_insert_id()

mapper.update:
update codegroup set
	cdgName = #{cdgName}
	,cdgNameEng = #{cdgNameEng}
	,cdgUseNy = #{cdgUseNy}
	,cdgUpdtDate = CURRENT_TIMESTAMP
	,cdgDesc = #{cdgDesc}
where
	cdgSeq = #{cdgSeq}
