mapper.sql:

	    from
			code a
			left join codegroup b on a.codegroup_cdgSeq = b.cdgSeq
		where 1=1
		AND a.cdUseNy = #{shUseNy}
        AND a.cdDelNy = #{shDelNy}

					AND a.cdInstDate BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.cdUpdtDate BETWEEN #{shDateStart} AND #{shDateEnd}


            AND b.cdgSeq = #{shValue}
            AND b.cdgName LIKE CONCAT('%',#{shValue},'%')
            AND b.cdgNameEng LIKE CONCAT('%',#{shValue},'%')
            AND a.cdSeq LIKE CONCAT('%',#{shValue},'%')
            AND a.cdName LIKE CONCAT('%',#{shValue},'%')
            AND a.cdNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into code (
	cdName
	,cdNameEng
	,cdUseNy
	,cdInstDate
	,cdDesc
	,codegroup_cdgSeq
) values (
	#{cdName}
	,#{cdNameEng}
	,#{cdUseNy}
	,CURRENT_TIMESTAMP
	,#{cdDesc}
	,#{codegroup_cdgSeq}
)

	SELECT last_insert_id()

mapper.update:
update code set
	cdName = #{cdName}
	,cdNameEng = #{cdNameEng}
	,cdUseNy = #{cdUseNy}
	,cdUpdtDate = CURRENT_TIMESTAMP
	,cdDesc = #{cdDesc}
	,codegroup_cdgSeq = #{codegroup_cdgSeq}
where
	cdSeq = #{cdSeq}
