mapper.sql:

	    from codegroup as a
	    where 1=1
	    AND a.cdgrUseNy = #{shUseNy}
        AND a.cdgrDelNy = #{shDelNy}


					AND a.cdgrRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.cdgrModDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.cdgrSeq = #{shValue}
            AND a.cdgrName LIKE CONCAT('%',#{shValue},'%')
            AND a.cdgrNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into codegroup (
	cdgrName
	,cdgrNameEng
	,cdgrUseNy
	,cdgrRegDate
	,cdgrDesc
) values (
	#{cdgrName}
	,#{cdgrNameEng}
	,#{cdgrUseNy}
	,CURRENT_TIMESTAMP
	,#{cdgrDesc}
)

	SELECT last_insert_id()

mapper.update:
update codegroup set
	cdgrName = #{cdgrName}
	,cdgrNameEng = #{cdgrNameEng}
	,cdgrUseNy = #{cdgrUseNy}
	,cdgrModDate = CURRENT_TIMESTAMP
	,cdgrDesc = #{cdgrDesc}
where
	cdgrSeq = #{cdgrSeq}
